import React, { PureComponent } from 'react';
import _ from 'lodash'

import './snow.scss'
import snowImage from './img/snowflake.png'


const snowImageCount = 45;
// import { Snow } from './snowStyled'
class SnowFallEffect extends PureComponent {

  constructor(props) {
    super(props)
    // this.excuteEffect = this.excuteEffect.bind(this)
  }

  // excuteEffect () {

  // }

  // componentDidMount () {
  //   this.excuteEffect()
  // }

  renderSnow() {
    let snows = []
    for (let i = 0; i < snowImageCount; i++) {
      snows.push(<snowflake key={`snow-flake-${i}`}><img src={snowImage} />Ô∏è</snowflake>)
    }
    return snows
  }

  render() {
    return (
      <div className={this.props.className || ''}>
        <snowfall>
          <snowflake><span>‚ùÑÔ∏è</span>Ô∏èÔ∏è</snowflake>
          {/* <snowflake><span>üçÇ</span>Ô∏èÔ∏è</snowflake> */}
          {/* <snowflake><span>üíù</span>Ô∏èÔ∏è</snowflake> */}
          {/* <snowflake><span>üåü</span>Ô∏è</snowflake> */}
          {this.renderSnow()}
        </snowfall>
      </div>
    )
  }
}

export default SnowFallEffect
