import React                from 'react';
import styled               from 'styled-components';

import { Colors }           from '../components/theme/Colors';
import history              from '../history';
import HostControlPanel     from '../components/HostControlPanel';
import Input            from '../components/inputs/Input'


const PageWrapper = styled.div`
  background-color: ${Colors.LightBlue};
  position: absolute;
  right: 0;
  left: 0;
  top: 0;
  bottom: 0;
`;

class MeetingRoomScreen extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      optionComponents: [<Input placeholder={'Option'} handleChange={this.handleChange}/>, <Input placeholder={'Option'} handleChange={this.handleChange}/>],
      optionTexts: []
    }
  }

  addOption = () => {
    this.setState({
      optionComponents: [...this.state.optionComponents, <Input placeholder={'Option'} handleChange={this.handleChange}/>]
    })
    console.log('Option added')
    console.log(this.state.optionComponents.length)
  }

  handleChange = () => {
    this.setState({
      optionTexts: []
    })
    for (const [index, value] of this.state.optionComponents) {
      this.state.optionTexts.push(<li key={index}>{value}</li>)
    }
  }

  handleCreatePoll = () => {
    alert('Creating poll' + this.state.optionTexts)
  }

  render() {
    return (
      <PageWrapper>
        <HostControlPanel width={300} title="Create a Poll"
                          handleSubmit={this.handleCreatePoll}
                          handleCreateOption={this.addOption}
                          handleChange={this.handleChange}
                          options={this.state.optionComponents} />
      </PageWrapper>
    );
  }

};

export default MeetingRoomScreen;
