import React                from 'react';
import styled               from 'styled-components';

import { Colors }           from '../components/theme/Colors';
import history              from '../history';
import HostControlPanel     from '../components/HostControlPanel';
import Input            from '../components/inputs/Input'


const PageWrapper = styled.div`
  background-color: ${Colors.LightBlue};
  position: absolute;
  right: 0;
  left: 0;
  top: 0;
  bottom: 0;
`;

class MeetingRoomScreen extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      optionComponents: [<InputRow widthRatios={[1, null]}>
            <Input placeholder={'Option'} onChange={this.handleChange}/>, <Input placeholder={'Option'} onChange={this.handleChange}/>]
            <Button>Delete</Button>
        </InputRow>],
      options: []
    }

    this.handleChange = this.handleChange.bind(this);
  }

  addOption = () => {
    this.setState({
      optionComponents: [...this.state.optionComponents, <Input placeholder={'Option'} onChange={this.handleChange}/>]
    })
    console.log('Option added')
    console.log(this.state.optionComponents.length)
  }

  handleChange = (event) => {
    console.log('Option value being changed')
    console.log(event.target.value)
    for (const component of this.state.optionComponents) {
      console.log(component.value)
    }
  }

  handleCreatePoll = () => {
    alert('Creating poll' + this.state.optionTexts)
    for (const component of this.state.optionComponents) {
      console.log(component.value)
    }
  }

  render() {
    return (
      <PageWrapper>
        <HostControlPanel width={300} title="Create a Poll"
                          handleSubmit={this.handleCreatePoll}
                          handleCreateOption={this.addOption}
                          handleChange={this.handleChange}
                          options={this.state.optionComponents} />
      </PageWrapper>
    );
  }

};

export default MeetingRoomScreen;
