import React            from 'react';
import styled           from 'styled-components';
import PropTypes        from 'prop-types';
import ExtraPropTypes   from 'react-extra-prop-types';

import { Colors }       from './theme/Colors';
import Body             from './theme/Body';
import Card             from './Card';
import Button           from './Button';



const propTypes = {
  value: PropTypes.string,
  handleChange: PropTypes.func,
  handleSubmit: PropTypes.func,
};

const defaultProps = {
  handleChange: undefined,
  handleSubmit: undefined
};

const ComponentWrapper = styled.div`
  ${({small}) => small && `padding-bottom: 16px`}
  ${({medium}) => medium && `padding-bottom: 20px`}
  ${({large}) => large && `padding-bottom: 26px`}
`;

const RoomCodeFormComponent = styled.div`
  width: 80%;
  border-radius: 5px;
`;

const renderButton = (props) => {

  const { small, medium, large } = props;

  let width;

  if (large) { width = 175}
  else if ( small ) { width = 125 }
  else { width = 150 }

  return(
    <ButtonWrapper>
      <Button small={small} medium={medium} large={large} width={width}>
        SUBMIT
      </Button>
    </ButtonWrapper>

  )
};

const renderInput = (props) => {
  const { label, small, medium, large, value, handleChange, handleSubmit } = props;

  return (
    <ComponentWrapper small={small} medium={medium} large={large}>
      <form onSubmit={handleSubmit}>
        <label>
          Room Code:
          <input type="text" value={value} onChange={handleChange} />
        </label>
        <input type="submit" value="Submit"/>
      </form>
    </ComponentWrapper>
  )
};

const RoomCodeForm = (props) => {

    const { width, value, handleChange, handleSubmit } = props;

    return (
      <Card width={width}>
        {renderInput(props)}
      </Card>


    );

};

RoomCodeForm.propTypes = propTypes;
RoomCodeForm.defaultProps = defaultProps;

export default RoomCodeForm;
