import React                from 'react';
import styled               from 'styled-components';

import { Colors }           from '../components/theme/Colors';
import history              from '../history';
import HostControlPanel     from '../components/HostControlPanel';
import Input            from '../components/inputs/Input'


const PageWrapper = styled.div`
  background-color: ${Colors.LightBlue};
  position: absolute;
  right: 0;
  left: 0;
  top: 0;
  bottom: 0;
`;

class MeetingRoomScreen extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      options: []
    }
  }

  addOption = () => {
    this.setState({
      polls: [...this.state.options, <PollAgendaCard width={800}
                                                   height={10}
                                                   type="Y/N"
                                                   title="Ammend wording on clause XYZ"
                                                   closed={true}
                                                   small={true} />, <PollAgendaCard width={800}
                                                                                                height={10}
                                                                                                type="Y/N"
                                                                                                title="Clause XYZ"
                                                                                                closed={true}
                                                                                                small={true} />]
    })
  }

  handleCreateOption() {
    this.state.options.append(<Input placeholder={'Option'}/>)
  }

  render() {
    return (
      <PageWrapper>
        <HostControlPanel width={300} title="Create a Poll" handleCreateOption={this.handleCreateOption} />

      </PageWrapper>
    );
  }

};

export default MeetingRoomScreen;
