import React                from 'react';
import styled               from 'styled-components';
import PollAgendaCard       from '../components/PollAgendaCard';

import { Colors }           from '../components/theme/Colors';
import history              from '../history'

const PageWrapper = styled.div`
  background-color: ${Colors.LightBlue};
  position: absolute;
  right: 0;
  left: 0;
  top: 0;
  bottom: 0;
`;

const User = () => {
  return <p>User</p>
}

class App extends React.Component {

  state = {
    users: []
  }

  addUser = () => {
    this.setState({
      users: [...this.state.users, <User />]
    })
  }

  render() {
    return (
      <div>
        <button onClick={this.addUser}>Add User</button>
        {this.state.users}
      </div>
    );
  }
}

const rootElement = document.getElementById("root");
ReactDOM.render(<App />, rootElement);

class MeetingRoomScreen extends React.Component {
  constructor(props) {
    super(props);

  }

  addPoll = () => {
    this.state({
      polls: [...this.state.polls, <PollAgendaCard width={800}
                                                   type="Y/N"
                                                   title="Ammend wording on clause XYZ"
                                                   closed={true}
                                                   small={true} />]
    })
  }

  render() {


    return (
        <PageWrapper>
          {this.state.polls}
          <button onClick={this.addPoll}>Add Poll</button>
        </PageWrapper>
      );
  }
};

export default MeetingRoomScreen;
