import React            from 'react';
import styled           from 'styled-components';
import PropTypes        from 'prop-types';
import ExtraPropTypes   from 'react-extra-prop-types';

import { Colors }       from './theme/Colors';
import Body             from './theme/Body';
import {Jumbo}             from './theme/Jumbo';
import Text             from './theme/Text'

import OptionGroup      from './OptionGroup';
import Card             from './Card';
import Button           from './Button';
import Input            from '../components/inputs/Input'
import TextArea            from '../components/inputs/TextArea'
import CheckBox         from './buttons/CheckBox'
import Option           from './options/Option'


const ComponentWrapper = styled.div`
  ${({small}) => small && `padding-bottom: 20px`}
  ${({medium}) => medium && `padding-bottom: 26px`}
  ${({large}) => large && `padding-bottom: 32px`}
`;

const SideBySideWrapper = styled.div`
  display: flex;
  flex-direction: row;
  justify-content: flex-start;
`;

const CenterWrapper = styled.div`
  display: flex;
  flex-direction: row;
  justify-content: center;
`;

export default class HostControlPanel extends React.Component {
  constructor(props) {
    super();

    this.state = {
      poll_options: []
    };
  }

  renderTitle(props) {
    const { title, small, medium, large } = props;

    return (
      <ComponentWrapper small={small} medium={medium} large={large}>
        <Jumbo threeExtraSmall color={Colors.Blue}>
          {title}
        </Jumbo>
      </ComponentWrapper>
    )
  };

  renderTitleInput(props) {
    const {small, medium, large } = props;

    return (
      <ComponentWrapper small={small} medium={medium} large={large}>
        <Input placeholder={'Enter poll title'}/>
      </ComponentWrapper>
    )
  };

  renderDescriptionInput(props) {
    const { small, medium, large } = props;

    return (
      <ComponentWrapper small={small} medium={medium} large={large}>
        <TextArea placeholder={'Enter poll description'}/>
      </ComponentWrapper>
    )
  };

  renderPollOptionsInput(props) {
    const { small, medium, large }  = props;

    return (
      <ComponentWrapper small={small} medium={medium} large={large}>
        <Jumbo fiveExtraSmall color={Colors.Blue}>
          {'Enter Poll Options'}
        </Jumbo>
        <Input placeholder={'Option 1'}/>
        <Input placeholder={'Option 2'}/>
        <Input placeholder={'Option 3'}/>
      </ComponentWrapper>
    )
  };

  renderResultViewingCheckbox(props)  {
    const { small, medium, large } = props;

    return (
      <ComponentWrapper small={small} medium={medium} large={large}>
        <SideBySideWrapper>
          <Option  buttonType={'checkbox'} />
          <Text> Do not show results </Text>
        </SideBySideWrapper>
      </ComponentWrapper>
    )
  };

  renderCreateButton(props) {
    const { small, medium, large } = props;

    return (
      <ComponentWrapper>
        <CenterWrapper>
          <Button small={small} medium={medium} large={large}
                  width={150} >
            {'Create Poll'}
          </Button>
        </CenterWrapper>
      </ComponentWrapper>
    )
  };

  render() {
    return (
      <Card width={width}>
        {this.renderTitle(props)}
        {this.renderTitleInput(props)}
        {this.renderDescriptionInput(props)}
        {this.renderPollOptionsInput(props)}
        {this.renderResultViewingCheckbox(props)}
        {this.renderCreateButton(props)}
      </Card>
    )
  }

}



