/*
 *  PollItem class just to store the info needed anywhere
 *  a poll needs to be shown
 *
 */
export default class PollItem {
  constructor() {
    this.options = ['yes', 'no', 'abstain'];
    this.title = '';
    this.description = '';
    this.showResults = true;
    this.order = -1;
    this.optionMap = {};
    this.type = '';
    this.status = 'pending';
  }

  logData() {
    console.log("Title: " + this.state.title);
    console.log("Description: " + this.state.description);
    console.log("Options: " + this.state.options);
  }

  setType(typ) {
    this.type = typ
  }

  setStatus(status) {
    this.status = status
  }

  setTitle(title) {
    this.title = title
  }

  setDescription(desc) {
    this.description = desc
  }

  addOption(option){
    this.options = [...this.state.options, option]
  }

  removeOption(option){
    console.log("Poll item delete option");
  }

  setShowResults(bool) {
    this.showResults = bool
  }

  setOrder(order) {
    this.order = order
  }

  setOptions() {
    for(var i = 0; i < this.options.length; i++) {
      this.optionMap[this.options[i]] = 0
    }
  }

  getInfo() {
    return {
      type: this.type,
      title: this.title,
      description: this.description,
      options: this.options
    }
  }
}
